import { Link, useNavigate } from "react-router-dom";
import { useState, useHistory } from "react";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };
  const isValidEmail = (email) => {
    // Vérification du format d'email avec une expression régulière
    // Cette expression régulière est une simplification et ne couvre pas tous les cas possibles
    // Vous pouvez utiliser une expression régulière plus complexe pour une validation plus précise
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailPattern.test(email);
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    if (email.trim() === "" || password.trim() === "") {
      setError("Veuillez remplir tous les champs.");
    }else if (!isValidEmail(email)) {
      setError("Veuillez saisir une adresse e-mail valide.");
    } else if (password.length < 8) {
      setError("Le mot de passe doit contenir au moins 8 caractères.");
    } else {
      navigate("/todo");
    }
  };

  return (
    <form className="text-center my-4 text-light" onSubmit={handleSubmit}>
      <h1 className="mb-4">Login Form</h1>
      <input
        type="text"
        className={`form-control mb-2`}
        id="email"
        placeholder="Email"
        value={email}
        onChange={handleEmailChange}
      />
      <input
        type="text"
        className={`form-control mb-3`}
        id="password"
        placeholder="Enter your Password"
        value={password}
        onChange={handlePasswordChange}
      />
      {error && <p className="text-danger">{error}</p>}

      <button type="submit" className="btn btn-dark">
        Login
      </button>
    </form>
  );
};
export default Login;
