        ...prevState,
        [e.target.name]: e.target.value,
    }));
}
  return (
    <form className="add text-center my-4" onSubmit={addItem}>
      <label htmlFor="add" className="add text-light">
        Add a new todo:
      </label>
      <input
        type="text"
        className="form-control m-auto"
        name="add"
        id="add"
        value={addForm.todo}
        onChange={handleChange}
      />

    </form>
  );
};
