import { useNavigate, useParams } from "react-router-dom";
import FloatingButton from "../UI/FloatingButton";
import { useContext } from "react";
import TodoContext from "../../context/todoContext";
const TodoDetailsItem = () => {
  const { id } = useParams();
  const ctx = useContext(TodoContext);
  const navigate = useNavigate();
  let todoItem = ctx.todoItems.find((item) => item.id === parseInt(id));

  return (
    <>
      <div className="container">
        <h1>todo details</h1>
        <h3></h3>
        Id: <p>{id}</p>
        Todo: <p>{todoItem.todo}</p>
        Complete: <p>{todoItem.complete.toString()}</p>
        <button class="btn" onClick={() => navigate(-1)}>
          Back
        </button>
      </div>
      <FloatingButton />
    </>
  );
};
export default TodoDetailsItem;
